import React from 'react'
import { Image, Text, View, StyleSheet, TouchableOpacity } from 'react-native'
import COLORS from '../consts/Colors';
import { SlugStr } from '../consts/Selector';
import { useNavigation } from '../utils/useNavigation';

export default function OderCard(props: any) {
    const product = props.product;
    const qty = props.qty;
    const status = props.status;
    const { navigate } = useNavigation();

    return (
        <View style={styles.container}>
            <View style={{ flex: 1 }}>
                <Image style={styles.img} source={{ uri: product.product_avatar }} />
            </View>
            <View style={styles.productContainer}>
                <View style={styles.productDetal}>
                    <Text style={styles.productName}>{product && SlugStr(product.product_title, 22)}</Text>
                </View>
                <Text style={{ fontSize: 16, color: '#222' }}>{SlugStr(product && product.product_description, 62)}</Text>
                <View style={styles.productPrice}>
                    <Text>{qty}</Text>
                    <Text style={{ color: '#222', fontSize: 20, fontWeight: 'bold' }}>{(product.product_price * (100 - product.product_sale) / 100) * qty}đ</Text>
                </View>
                {
                    status === 2 &&
                    <TouchableOpacity onPress={() => {
                        navigate('Complaint', { id: product.product_id });
                    }}>
                        <Text style={styles.btnReport}>Báo cáo</Text>
                    </TouchableOpacity>
                }
            </View>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'row',
        padding: 15,
        backgroundColor: '#fff',
        marginTop: 3,
        borderRadius: 10,
        borderBottomColor: COLORS.primary,
        borderBottomWidth: 1
    },
    btnReport: {
        backgroundColor: 'red',
        padding: 7,
        width: 150,
        borderRadius: 20,
        marginBottom: 10,
        fontSize: 18,
        textAlign: 'center',
        fontWeight: '700',
        color: '#fff'
    },
    img: {
        flex: 1,
        borderRadius: 15,
        borderColor: COLORS.primary,
        borderWidth: 1
    },
    productContainer: {
        flex: 2,
        flexDirection: 'column',
        marginLeft: 10
    },
    productDetal: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 8
    },
    productName: {
        fontSize: 22,
        fontWeight: 'bold',
        color: '#111'
    },
    iconDelete: {
        backgroundColor: COLORS.primary,
        padding: 5,
        borderRadius: 8
    },
    productPrice: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginTop: 8
    }
});
