import React, { useState, useEffect } from 'react'
import { Image, Text, TouchableOpacity, View, StyleSheet } from 'react-native'
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import NumericInput from 'react-native-numeric-input';
import COLORS from './../consts/Colors';
import { SlugStr } from './../consts/Selector';
import { ProductModel, State } from '../redux';
import { borderWidth } from 'styled-system';
import { useDispatch, useSelector } from 'react-redux';

export default function CartCard(props: any) {
    let [qty, setNumber] = useState<number>();
    const [product, setProduct] = useState<ProductModel>();
    const cartState = useSelector((state: State) => state.cartReducer);
    const dispatch = useDispatch();

    useEffect(() => {
        setNumber(props.qty);
        setProduct(props.product);
    },[]);

    useEffect(() => {
        setNumber(props.qty);
        setProduct(props.product);
    },[cartState])

    const onTap_btn = (value: number) => {
        props.onTap(product.product_id, value);
    }

    return (
        product ?
        <View style={styles.container}>
            <View style={{ flex: 1 }}>
                <Image style={styles.img} source={{ uri: product.product_avatar }} />
            </View>
            <View style={styles.productContainer}>
                <View style={styles.productDetal}>
                    <Text style={styles.productName}>{ product && SlugStr( product.product_title, 22)}</Text>
                    <TouchableOpacity style={styles.iconDelete} onPress={() => {
                        onTap_btn(0);
                    }}>
                        <MaterialIcons name="delete" size={24} color="white" />
                    </TouchableOpacity>
                </View>
                <Text style={{ fontSize: 16, color: '#222' }}>{SlugStr(product && product.product_description, 62)}</Text>
                <View style={styles.productPrice}>
                    <TouchableOpacity
                        style={{
                            width: 16,
                            borderColor: 'black',
                            borderWidth: 1,
                            justifyContent: 'center',
                            alignItems: 'center',
                            minWidth: 33,
                        }}
                        onPress={() => {
                            qty--;
                            onTap_btn(qty);
                        }}
                    >
                        <Text
                        >-</Text>
                    </TouchableOpacity>

                    <Text>{qty}</Text>

                    <TouchableOpacity
                        style={{
                            width: 16,
                            borderColor: 'black',
                            borderWidth: 1,
                            justifyContent: 'center',
                            alignItems: 'center',
                            minWidth: 33,
                        }}
                        onPress={() => {
                            qty++;
                            onTap_btn(qty);
                        }}>
                        <Text>+</Text>
                    </TouchableOpacity>
                    <Text style={{ color: '#222', fontSize: 20, fontWeight: 'bold' }}>{(product.product_price * (100 - product.product_sale) / 100) * qty}đ</Text>
                </View>
            </View>
        </View>
        :
        <View></View>
    )
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'row',
        padding: 15,
        backgroundColor: '#fff',
        marginTop: 3,
        borderRadius: 10,
        borderBottomColor: COLORS.primary,
        borderBottomWidth: 1
    },
    img: {
        flex: 1,
        borderRadius: 15,
        borderColor: COLORS.primary,
        borderWidth: 1
    },
    productContainer: {
        flex: 2,
        flexDirection: 'column',
        marginLeft: 10
    },
    productDetal: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 8
    },
    productName: {
        fontSize: 22,
        fontWeight: 'bold',
        color: '#111'
    },
    iconDelete: {
        backgroundColor: COLORS.primary,
        padding: 5,
        borderRadius: 8
    },
    productPrice: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginTop: 8
    }
});
